<script lang='ts'>
	import type { PlotlyHTMLElement, Data, Layout, Template } from 'plotly.js';
	import { store as theme } from '$lib/DarkMode.svelte';

	import { onMount, afterUpdate } from 'svelte';
	import { v4 as uuid } from 'uuid';

	export let data: Data[] = [];
	export let layout: Partial<Layout> = {};

	const id = uuid();

	let plot: PlotlyHTMLElement;

	onMount(async () => {
		const added = document.querySelector('#plotly-script') !== null;
		const loaded = added && 'Plotly' in globalThis;

		console.log('added:', added, 'loaded:', loaded);

		if (!added) {
			console.log('adding!');
			const script = document.createElement('script') as HTMLScriptElement;
			script.src = 'https://cdn.plot.ly/plotly-2.8.3.min.js';
			script.id = 'plotly-script';
			document.querySelector('head').appendChild(script);
			await new Promise<void>(res => {
				script.addEventListener('load', () => res(), { once: true });
			});
		}

		if (!loaded) {
			console.log('waiting for load!');
			await new Promise<void>(async res => {
				while (!('Plotly' in globalThis)) {
					await new Promise(r => setTimeout(r, 100));
				}
				res();
			});
		}

		plot = await Plotly.newPlot(document.getElementById(id), data, { template: $theme === 'dark' ? plotlyDark : plotlyWhite, ...layout });
	});

	afterUpdate(async () => {
		try { document.documentElement.removeChild(plot); } catch (ex) { console.info(ex); }
		if ('Plotly' in globalThis) plot = await Plotly.newPlot(document.getElementById(id), data, { template: $theme === 'dark' ? plotlyDark : plotlyWhite, ...layout });
	});
</script>

<div {id} />